import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Switch, Link, Redirect } from 'react-router-dom';
import ExpenseList from './ExpenseList';
import ExpenseForm from './ExpenseForm';
import Login from './Login';

const App = () => {
  const [user, setUser] = useState(null);
  const [redirectToAdd, setRedirectToAdd] = useState(false);

  const handleLogin = (userData) => {
    setUser(userData);
  };

  const handleAddExpense = () => {
    // Perform the logic for adding an expense
    // After successfully adding an expense, setRedirectToAdd to true
    setRedirectToAdd(true);
  };

  return (
    <Router>
      <div>
        {user && (
          <nav>
            <Link to="/expenses">Expense List</Link>
            <Link to="/expenses/new">Add Expense</Link>
          </nav>
        )}

        <Switch>
          <Route path="/" exact>
            {user ? <Redirect to="/expenses" /> : <Login onLogin={handleLogin} />}
          </Route>
          <Route path="/expenses" exact>
            {user ? <ExpenseList redirectToAdd={redirectToAdd} /> : <Redirect to="/login" />}
          </Route>
          <Route path="/expenses/new">
            {user ? <ExpenseForm onAddExpense={handleAddExpense} /> : <Redirect to="/login" />}
          </Route>
        </Switch>
      </div>
    </Router>
  );
};

export default App;
