import React, { useState } from 'react';
import { useHistory } from 'react-router-dom';
import axios from 'axios';
import './ExpenseForm.css';

const ExpenseForm = () => {
  const [category, setCategory] = useState('');
  const [paymentMethod, setPaymentMethod] = useState('');
  const [description, setDescription] = useState('');
  const [amount, setAmount] = useState('');
  const history = useHistory();

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const token = localStorage.getItem(''); // Fetch the JWT token from storage
      if (!token) {
        // Handle the case where the token is not available
        console.error('JWT token not found.');
        return;
      }

      // Assuming you have user_id and category_id set appropriately
      const newExpense = {
        category,
        payment_method: paymentMethod,
        description,
        amount: parseFloat(amount),
        user_id: 1, // Replace with the actual user ID, fetched from the JWT token
        category_id: 1,
      };

      await axios.post('http://localhost:5000/expenses', newExpense, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });

      // Redirect to the expense list page after successful submission
      history.push('/expenses');
    } catch (error) {
      console.error('Error submitting expense:', error);
    }
  };

  return (
    <div className="expense-form-container">
      {/* ... */}
      <form onSubmit={handleSubmit}>
        {/* ... */}
        <button className="expense-form-button" type="submit">
          Submit
        </button>
      </form>
    </div>
  );
};

export default ExpenseForm;
