import React, { useState } from 'react';
import axios from 'axios';
import { useHistory } from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.min.css'; // Import Bootstrap CSS
import './Login.css';
import Logo from './logo.png';

const Login = ({ setUser }) => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [errorMessage, setErrorMessage] = useState(''); // New state for error message
  const history = useHistory();

  const handleSubmit = async (e) => {
    e.preventDefault();
    setErrorMessage(''); // Reset error message on new submission

    try {
      const response = await axios.post('http://localhost:5000/login', {
        username,
        password,
      });

      setUser(response.data);
      history.push('/expenses');
    } catch (error) {
      console.error('Error logging in:', error);
      // Set the error message for display
      setErrorMessage('Invalid username or password'); // Customize this message as needed
    }
  };

  return (
    <section className="login mt-5">
      <div className="container">
        <div className="row justify-content-center">
          <div className="col-md-6 login__container p-4 shadow">
            <img src={Logo} alt="Logo" className="login__logo mb-4" />

            <form onSubmit={handleSubmit} className="login__form">
              {errorMessage && <div className="alert alert-danger">{errorMessage}</div>}
              <div className="mb-3">
                <label htmlFor="username" className="form-label">
                  Username
                </label>
                <input
                  type="text"
                  id="username"
                  value={username}
                  onChange={(e) => setUsername(e.target.value)}
                  required
                  className="form-control"
                />
              </div>
              <div className="mb-3">
                <label htmlFor="password" className="form-label">
                  Password
                </label>
                <input
                  type="password"
                  id="password"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  required
                  className="form-control"
                />
              </div>
              <button type="submit" className="btn btn-primary">
                Login
              </button>
            </form>
          </div>
        </div>
      </div>
    </section>
  );
};

export default Login;
