// App.js
import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Switch, Link, useHistory, useLocation } from 'react-router-dom';
import ExpenseList from './ExpenseList';
import ExpenseForm from './ExpenseForm';
import Login from './Login';

const Navigation = ({ user, isExpenseListPage }) => {
  return (
    <nav>
      {user && !isExpenseListPage && (
        <Link to="/expenses">Expense List</Link>
      )}
    </nav>
  );
};

const App = () => {
  const [user, setUser] = useState(null);
  const [expenses, setExpenses] = useState([]);
  const history = useHistory();

  const handleAddExpense = (newExpense) => {
    // Assuming newExpense is an object with 'description' and 'amount' properties
    setExpenses([...expenses, newExpense]);

    // Redirect to ExpenseList after adding the expense
    history.push('/expenses');
  };

  const location = useLocation();

  // Check if the current path is the expense list page
  const isExpenseListPage = location.pathname === '/expenses';

  return (
    <Router>
      <div>
        <Navigation user={user} isExpenseListPage={isExpenseListPage} />

        <Switch>
          <Route path="/" exact>
            {user ? <ExpenseList expenses={expenses} /> : <Login setUser={setUser} />}
          </Route>
          <Route path="/expenses" exact>
            <ExpenseList expenses={expenses} />
          </Route>
          <Route path="/expenses/new">
            <ExpenseForm onAddExpense={handleAddExpense} />
          </Route>
        </Switch>
      </div>
    </Router>
  );
};

export default App;
