// src/components/App.js
import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Switch, Link, Redirect } from 'react-router-dom';
import axios from 'axios';
import ExpenseList from './ExpenseList';
import ExpenseForm from './ExpenseForm';
import Auth from './Auth'; // Unified authentication component
import Logout from './Logout';

const App = () => {
  const [user, setUser] = useState(null);

  useEffect(() => {
    // Fetch user info or check authentication status here
    // ...

  }, []);

  return (
    <Router>
      <div>
        <nav>
          <Link to="/">Home</Link>
          {user ? (
            <>
              <Link to="/expenses">Expense List</Link>
              <Link to="/expenses/new">Add Expense</Link>
              <Link to="/logout">Logout</Link>
            </>
          ) : (
            <>
              <Link to="/auth">Login / Register</Link>
            </>
          )}
        </nav>

        <Switch>
          <Route path="/" exact>
            <h1>Welcome to the Expense Tracker</h1>
          </Route>
          <Route path="/auth">
            <Auth setUser={setUser} />
          </Route>
          <Route path="/logout">
            <Logout setUser={setUser} />
          </Route>
          <Route path="/expenses" exact>
            {user ? <ExpenseList /> : <Redirect to="/auth" />}
          </Route>
          <Route path="/expenses/new">
            {user ? <ExpenseForm /> : <Redirect to="/auth" />}
          </Route>
        </Switch>
      </div>
    </Router>
  );
};

export default App;
