import React, { useState } from 'react';
import axios from 'axios';

const IncomeForm = () => {
  const [incomeData, setIncomeData] = useState({
    user_id: '',
    source: '',
    amount: '',
  });

  const handleChange = (e) => {
    setIncomeData({ ...incomeData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:5000/income', incomeData);
      alert(response.data.message);
    } catch (error) {
      alert('Error posting income data');
    }
  };

  return (
    <div>
      <h2>Add Income</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>User ID:</label>
          <input
            type="text"
            name="user_id"
            value={incomeData.user_id}
            onChange={handleChange}
          />
        </div>
        <div>
          <label>Source:</label>
          <input
            type="text"
            name="source"
            value={incomeData.source}
            onChange={handleChange}
          />
        </div>
        <div>
          <label>Amount:</label>
          <input
            type="number"
            name="amount"
            value={incomeData.amount}
            onChange={handleChange}
          />
        </div>
        <button type="submit">Submit</button>
      </form>
    </div>
  );
};

export default IncomeForm;
