import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Switch, Link, useHistory } from 'react-router-dom';
import IncomeList from './IncomeList';
import IncomeForm from './IncomeForm';
import ExpenseList from './ExpenseList';
import ExpenseForm from './ExpenseForm';
import Login from './Login';

const App = () => {
  const [user, setUser] = useState(null);
  const [expenses, setExpenses] = useState([]);
  const history = useHistory();
  const [incomes, setIncomes] = useState([]);

  const handleAddExpense = (newExpense) => {
    // Assuming newExpense is an object with 'description' and 'amount' properties
    setExpenses([...expenses, newExpense]);

    // Redirect to ExpenseList after adding the expense
    history.push('/expenses');
  };

  const handleIncomeAdded = (newIncome) => {
    setIncomes([...incomes, newIncome]);
    history.push('/incomes');
  };

  return (
    <Router>
      <div>
        {user && (
          <nav>
            <Link to="/expenses">Expense List</Link>
            <Link to="/expenses/new">Add Expense</Link>
            <Link to="/incomes">Income List</Link>
            <Link to="/income/new">Add Income</Link>
          </nav>
        )}

        <Switch>
          <Route path="/" exact>
            {user ? <ExpenseList expenses={expenses} /> : <Login setUser={setUser} />}
          </Route>
          <Route path="/expenses" exact>
            <ExpenseList expenses={expenses} />
          </Route>
          <Route path="/expenses/new">
            <ExpenseForm onAddExpense={handleAddExpense} />
          </Route>
          <Route path="/income/new">
            <IncomeForm onIncomeAdded={handleIncomeAdded} />
          </Route>
          {/* Add route for IncomeList if it exists */}
          <Route path="/incomes">
            <IncomeList incomes={incomes} />
          </Route>
        </Switch>
      </div>
    </Router>
  );
};

export default App;
