// src/components/Login.js
import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import './Login.css';

const Login = ({ setUser }) => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [isLogin, setIsLogin] = useState(true);

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const endpoint = isLogin ? 'http://localhost:5000/login' : 'http://localhost:5000/register';

      const response = await axios.post(endpoint, {
        username,
        password,
      });

      setUser(response.data);
    } catch (error) {
      console.error(`Error ${isLogin ? 'logging in' : 'registering'}:`, error);
    }
  };

  const toggleForm = () => {
    setIsLogin(!isLogin);
  };

  return (
    <section className="login-page">
      <div className="container">
        <div className="login-form">
          <form onSubmit={handleSubmit}>
            <div className="form-group">
              <label htmlFor="username">Username</label>
              <input
                type="text"
                id="username"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
                required
              />
            </div>
            <div className="form-group">
              <label htmlFor="password">Password</label>
              <input
                type="password"
                id="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                required
              />
            </div>
            <div className="form-group">
              <button type="submit">{isLogin ? 'Login' : 'Register'}</button>
            </div>
          </form>

          <p onClick={toggleForm} className="toggle-form-link">
            {isLogin ? 'Create an account' : 'Already have an account? Login'}
          </p>

          {/* Sign up link that takes user to the registration page */}
          {isLogin && (
            <p>
              Don't have an account? <Link to="/register">Sign up</Link>
            </p>
          )}

          {/* Button to go to the register form */}
          {isLogin && (
            <p>
              {' '}
              <Link to="/register">
                <button type="button">Register</button>
              </Link>
            </p>
          )}
        </div>
      </div>
    </section>
  );
};

export default Login;
