import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Switch, Link, useHistory, useLocation } from 'react-router-dom';
import ExpenseList from './ExpenseList';
import ExpenseForm from './ExpenseForm';
import Login from './Login';
import IncomeForm from './IncomeForm';

const Navigation = ({ onLogout }) => {
  const location = useLocation();
  const isExpenseListRoute = location.pathname === '/expenses';
  const isIncomeFormRoute = location.pathname === '/income/new';

  return (
    <nav>
      <Link to="/">Home</Link>
      {!isExpenseListRoute && <Link to="/expenses">Expense List</Link>}
      {!isIncomeFormRoute && <Link to="/income/new">Add Income</Link>}
      <button onClick={onLogout}>Logout</button>
    </nav>
  );
};

const App = () => {
  const [user, setUser] = useState(null);
  const [expenses, setExpenses] = useState([]);
  const history = useHistory();

  const handleAddExpense = (newExpense) => {
    setExpenses([...expenses, newExpense]);
    history.push('/expenses');
  };

  const handleLogout = () => {
    setUser(null);
    history.push('/');
  };

  return (
    <Router>
      <div>
        {user ? <Navigation onLogout={handleLogout} /> : (
          <nav>
            <Link to="/">Login</Link>
          </nav>
        )}

        <Switch>
          <Route path="/" exact>
            {user ? <ExpenseList expenses={expenses} /> : <Login setUser={setUser} />}
          </Route>
          <Route path="/expenses" exact>
            <ExpenseList expenses={expenses} />
          </Route>
          <Route path="/expenses/new">
            <ExpenseForm user_id={user?.user_id} />
          </Route>
          <Route path="/income/new">
            <IncomeForm user_id={user?.user_id} />
          </Route>
        </Switch>
      </div>
    </Router>
  );
};

export default App;
