// src/components/IncomeList.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

const IncomeList = () => {
  const [incomes, setIncomes] = useState([]);

  useEffect(() => {
    const fetchIncomes = async () => {
      try {
        const response = await axios.get('http://localhost:5000/incomes');
        setIncomes(response.data);
      } catch (error) {
        console.error('Error fetching incomes:', error);
      }
    };

    fetchIncomes();
  }, []);

  const handleDelete = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/incomes/${id}`);
      // Optionally: Fetch the updated list of incomes and update the state
      // const response = await axios.get('http://localhost:5000/incomes');
      // setIncomes(response.data);
    } catch (error) {
      console.error('Error deleting income:', error);
    }
  };

  const totalIncome = incomes.reduce((total, income) => total + income.amount, 0)

  return (
    <div>
      <h2>Income List</h2>
      <ul>
        {incomes.map((income) => (
          <li key={income.id}>
            {income.source} - ${income.amount} - {new Date(income.date_received).toLocaleDateString()}
            <button onClick={() => handleDelete(income.id)}>Delete</button>
          </li>
        ))}
      </ul>
      <p>Total Income: ${totalIncome}</p>

      {/* Button to navigate to the income form */}
      <Link to="/incomes/new">
        <button>Add Income</button>
      </Link>
    </div>
  );
};

export default IncomeList;
