// Import necessary dependencies
import React, { useState } from 'react';
import axios from 'axios';
import './Login.css';

const Login = ({ setUser }) => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      // Update the endpoint to match your server's login endpoint
      const response = await axios.post('http://localhost:5000/login', {
        username,
        password,
      });

      setUser(response.data);
    } catch (error) {
      console.error('Error logging in:', error);
    }
  };

  const handleCreateAccount = () => {
    // Add logic to navigate to the registration page or display a registration form
    console.log('Redirect to registration page or show registration form');
  };

  return (
    <section className="login-page">
      <div className="container">
        <div className="login-form">
          <form onSubmit={handleSubmit}>
            <div className="form-group">
              <label htmlFor="username">Username</label>
              <input
                type="text"
                id="username"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
                required
              />
            </div>
            <div className="form-group">
              <label htmlFor="password">Password</label>
              <input
                type="password"
                id="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                required
              />
            </div>
            <div className="form-group">
              <button type="submit">Login</button>
            </div>
          </form>

          {/* Prompt to create an account */}
          <div className="create-account-prompt">
            <p>Don't have an account? </p>
            <button onClick={handleCreateAccount}>Create Account</button>
          </div>
        </div>
      </div>
    </section>
  );
};

export default Login;
