import React, { useState } from 'react';
import { useHistory } from 'react-router-dom';
import axios from 'axios';
import './ExpenseForm.css';

const ExpenseForm = () => {
  const [category, setCategory] = useState('');
  const [paymentMethod, setPaymentMethod] = useState('');
  const [description, setDescription] = useState('');
  const [amount, setAmount] = useState('');
  const history = useHistory();

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const token = localStorage.getItem('access_token');
      console.log
      if (!token) {
        console.error('JWT token not found.');
        return;
      }

      const newExpense = {
        category,
        payment_method: paymentMethod,
        description,
        amount: parseFloat(amount),
        user_id: 1, // Replace with the actual user ID, fetched from the JWT token
        category_id: 1,
      };

      await axios.post('http://localhost:5000/expenses', newExpense, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });

      history.push('/expenses');
    } catch (error) {
      console.error('Error submitting expense:', error);
    }
  };

  return (
    <div className="expense-form-container">
      <h2 className="expense-form-heading">Add Expense</h2>
      <form onSubmit={handleSubmit}>
        <label className="expense-form-label">
          Category:
          <select
            className="expense-form-select"
            value={category}
            onChange={(e) => setCategory(e.target.value)}
          >
            <option value="">Select a category</option>
            <option value="Groceries">Groceries</option>
            <option value="Transport">Transport</option>
            <option value="Utilities">Utilities</option>
            <option value="Entertainment">Entertainment</option>
            <option value="Grooming">Grooming</option>
            <option value="Health">Health</option>
          </select>
        </label>
        <br />
        <label className="expense-form-label">
          Payment Method:
          <select
            className="expense-form-select"
            value={paymentMethod}
            onChange={(e) => setPaymentMethod(e.target.value)}
          >
            <option value="">Select a payment method</option>
            <option value="Cash">Cash</option>
            <option value="Credit Card">Credit Card</option>
            <option value="Mpesa">Mpesa</option>
            <option value="Airtel Money">Airtel Money</option>
          </select>
        </label>
        <br />
        <label className="expense-form-label">
          Description:
          <input
            className="expense-form-input"
            type="text"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
          />
        </label>
        <br />
        <label className="expense-form-label">
          Amount:
          <input
            className="expense-form-input"
            type="number"
            value={amount}
            onChange={(e) => setAmount(e.target.value)}
          />
        </label>
        <br />
        <button className="expense-form-button" type="submit">
          Submit
        </button>
      </form>
    </div>
  );
};

export default ExpenseForm;
